import React, { useState } from "react";
import './FakeNewsAnalyzer.css';

const FakeNewsAnalyzer = () => {
  const [inputText, setInputText] = useState("");
  const [prediction, setPrediction] = useState(null);
  const [explanation, setExplanation] = useState("");
  const [shapPlot, setShapPlot] = useState(""); // To store the base64 image

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSubmit = async () => {
    try {
      // Send a request to the backend for prediction and explanation
      const response = await fetch("http://localhost:5000/explain", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ text: inputText }),
      });
      
      const data = await response.json();

      if (data.error) {
        alert(data.error);
        return;
      }

      // Set prediction, explanation, and shap_plot data
      setPrediction(data.label);
      setExplanation(data.explanation_text); // Display explanation text
      setShapPlot(data.shap_plot); // Set the base64-encoded image for the SHAP plot
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="analyzer-container">
      <h1 className="title">Fake News Detector</h1>
      <textarea
        value={inputText}
        onChange={handleInputChange}
        placeholder="Enter news title or text here"
      />
      <button onClick={handleSubmit}>Analyze</button>
      
      {prediction && (
        <div className="result">
          <h2>Prediction: {prediction}</h2>
          
          {/* Display the explanation text */}
          {explanation && (
            <div className="explanation">
              <h3>Explanation:</h3>
              <p>{explanation}</p>
            </div>
          )}

          {/* Display SHAP plot */}
          {shapPlot && (
            <div className="shap-plot">
              <h3>SHAP Explanation:</h3>
              <img src={`data:image/png;base64,${shapPlot}`} alt="SHAP Explanation" />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default FakeNewsAnalyzer;
