import React, { useState } from "react";
import './FakeNewsAnalyzer.css';

const FakeNewsAnalyzer = () => {
  const [inputText, setInputText] = useState("");
  const [prediction, setPrediction] = useState(null);
  const [explanation, setExplanation] = useState("");
  const [shapPlotPath, setShapPlotPath] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSubmit = async () => {
    setLoading(true);
    setError("");
    setPrediction(null);
    setExplanation("");
    setShapPlotPath("");

    try {
      const response = await fetch("http://localhost:5000/explain", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ text: inputText }),
      });

      if (!response.ok) {
        throw new Error("Failed to fetch explanation from the server.");
      }

      const data = await response.json();

      if (data.error) {
        throw new Error(data.error);
      }

      setPrediction(data.label);
      setExplanation(data.textual_explanation);
      setShapPlotPath(data.plot_path); // Set SHAP plot path
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="analyzer-container">
      <h1 className="title">Fake News Detector</h1>
      <textarea
        value={inputText}
        onChange={handleInputChange}
        placeholder="Enter news title or text here"
        className="input-textarea"
      />
      <button onClick={handleSubmit} disabled={loading} className="submit-button">
        {loading ? "Analyzing..." : "Analyze"}
      </button>

      {error && <p className="error">Error: {error}</p>}

      {prediction && (
        <div className="prediction-result">
          <h2>Prediction: {prediction}</h2>
          <p className="explanation">Explanation: {explanation}</p>
          <h3>SHAP Explanation Plot:</h3>
          <img
            src={`http://localhost:5000${shapPlotPath}`}
            alt="SHAP Explanation"
            className="shap-plot"
          />
        </div>
      )}
    </div>
  );
};

export default FakeNewsAnalyzer;
