import React, { useState } from "react";
import styles from './styles/FakeNewsAnalyzermodule.css';

const FakeNewsAnalyzer = () => {
  const [inputText, setInputText] = useState("");
  const [prediction, setPrediction] = useState(null);
  const [explanation, setExplanation] = useState("");

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSubmit = async () => {
    // Send a request to the backend for prediction
    const response = await fetch("http://localhost:5000/predict", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ text: inputText }),
    });
    const data = await response.json();
    setPrediction(data.label);
    setExplanation(data.explanation);
  };

  return (
    <div className={styles.analyzerContainer}>
    <h1 className={styles.title}>Fake News Detector</h1>
      <textarea
        value={inputText}
        onChange={handleInputChange}
        placeholder="Enter news title or text here"
      />
      <button onClick={handleSubmit}>Analyze</button>
      {prediction && (
        <div className="result">
          <h2>Prediction: {prediction}</h2>
          <p>Explanation: {explanation}</p>
        </div>
      )}
    </div>
  );
};

export default FakeNewsAnalyzer;
